CREATE TABLE estaciones (id int(255) NOT NULL auto_increment,Longitude varchar(255) NOT NULL,Latitude varchar(255) NOT NULL,Descripcion varchar(255) NOT NULL DEFAULT 'Red Siata',Foto varchar(255) NOT NULL DEFAULT 'sol',Red varchar(255) NOT NULL,Ip varchar(255) NOT NULL,NombreEstacion varchar(255) NOT NULL,DireccionCalleCarrera varchar(255) NULL,NombreContacto varchar(255) NULL,TelefonoContacto varchar(255) NULL,AlturaNivelMar varchar(255) NULL,AlturaNivelPiso varchar(255) NULL,Codigo varchar(255) NULL,P int(2) NULL,T int(2) NULL,H int(2) NULL,V int(2) NULL,D int(2) NULL,Subcuenca varchar(255) NULL,N int(2) NULL,Ciudad varchar(255) NULL,Barrio varchar(255) NULL,Comuna varchar(255) NULL,calidad int(10) NOT NULL DEFAULT '1',FechaInstalacion date NULL,telefono char(12) NULL,observaciones varchar(2000) NULL,estado varchar(1) NULL,fechaUltimaCalibracionVolumetrica date NULL,factorConversionPrecipitacionP1 float NULL,factorConversionPrecipitacionP2 float NULL,resolucion float NULL,tipoTarjeta varchar(25) NULL,fechaUltimaModificacion timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,scaleP float NULL,scalePR float NULL,scaleN float NULL,offsetN float NULL,offsetPR float NULL,offsetP float NULL,bancallena float NULL,PR int(1) NULL,vereda varchar(255) NULL,corregimiento varchar(255) NULL,operador varchar(20) NOT NULL DEFAULT 'comcel',bancamasllena float NULL,camara int(11) NULL,numeroplaca varchar(10) NULL,HS1 int(2) NULL,HS2 int(2) NULL,HS3 int(2) NULL,scaleP2 float NULL,offsetP2 float NULL,scaleHSA float NULL,offsetHSA float NULL,PHS1 float NULL,PHS2 float NULL,PHS3 float NULL,scaleVar1 int(4) NULL,scaleVar2 int(4) NULL,scaleVar3 int(4) NULL,N2 int(1) NULL,puerto_comunicacion int(7) NULL DEFAULT '-999',protocolo_comunicacion varchar(10) NULL,SV int(11) NULL,minor_flooding float NULL DEFAULT '-999',moderate_flooding float NULL DEFAULT '-999',major_flooding float NULL DEFAULT '-999',action_level float NULL DEFAULT '-999',fecha_cambio_sensor date NULL,hd int(1) NOT NULL,PRIMARY KEY (id));

CREATE TABLE datos (id int(255) NOT NULL,fecha date NOT NULL DEFAULT '1000-01-01 00:00:00',hora time NOT NULL DEFAULT '00:00:00',cliente int(255) NOT NULL,P1 float NOT NULL DEFAULT '-999',P2 float NOT NULL DEFAULT '-999',PR float NOT NULL DEFAULT '-999',calidad int(10) NULL DEFAULT '1',NI float NULL DEFAULT '-999',source varchar(1) NULL, PRIMARY KEY (fecha,hora,cliente));
